import React, { useState } from "react";
import axios from "axios";

const Weather = () => {
  const [city, setCity] = useState("");
  const [forecast, setForecast] = useState(null);
  const API_KEY = "9be36664e75a27c37390d54f6b3398f6";

  const fetchForecast = async () => {
    if (!city) return;
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY}&units=metric`
      );
      setForecast(response.data);
    } catch (error) {
      alert("City not found");
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Check the Weather Forecast</h2>
      <input
        type="text"
        placeholder="Enter city name"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <button onClick={fetchForecast}>Show Forecast</button>

      {forecast && (
        <div>
          <h3>{forecast.city.name}, {forecast.city.country}</h3>
          <div style={{ display: "flex", justifyContent: "center", flexWrap: "wrap" }}>
            {forecast.list.slice(0, 5).map((item, index) => (
              <div key={index} style={{ border: "1px solid #ccc", padding: "10px", margin: "5px", borderRadius: "5px" }}>
                <p><strong>{new Date(item.dt * 1000).toLocaleDateString()}</strong></p>
                <p>Temperature: {item.main.temp}Â°C</p>
                <p>Humidity: {item.main.humidity}%</p>
                <p>Description: {item.weather[0].description}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Weather;
